  <defs>
    <linearGradient id="leafGradient" gradientTransform="rotate(90)">
      <stop offset="0%" stop-color="#f5f5f5">
        <animate attributeName="stop-color" values="#f5f5f5; #8FAF8F"  dur="5s" fill="freeze" />
      </stop>
      <stop offset="100%" stop-color="#f5f5f5">
        <animate attributeName="stop-color" values="#f5f5f5; #3C493C" begin="5s" dur="5s" fill="freeze" />
      </stop>
    </linearGradient>
  </defs>

  <style>
    .animated-echinacea path {
      display: none;
      transition: 1s ease-in-out;
    }

    .stem-group {
      transform-origin: bottom center;
    }
  </style>

  <script type="text/javascript">
    const defs = document.querySelector('defs');

    function animatePath(path, animation_duration = 1000, animation_delay = 0) {
      const path_length = path.getTotalLength();
      path.style.strokeDasharray = path_length;
      path.style.strokeDashoffset = path_length;
      path.style.display = 'unset';
      path.animate([
        { strokeDashoffset: path_length },
        { strokeDashoffset: 0 }
      ], {
        duration: animation_duration,
        delay: animation_delay,
        easing: 'ease-in-out',
        fill: 'forwards'
      });
    }

    function animateGradient(path, background_color = '#f5f5f5', stop_1_color, stop_2_color, stop_1_percent = '0%') {
      //Create Leaf Gradient
      let new_gradient = document.createElementNS('http://www.w3.org/2000/svg', 'linearGradient');
      new_gradient.id = ( Date.now() / Math.random(100) );
      new_gradient.setAttribute('gradientTransform', 'rotate(90)');
      defs.appendChild(new_gradient);
      //Create the first stop
      let stop1 = document.createElementNS('http://www.w3.org/2000/svg', 'stop');
      stop1.setAttribute('offset', stop_1_percent);
      stop1.setAttribute('stop-color', background_color);
      new_gradient.appendChild(stop1);
      //Create the second stop
      let stop2 = document.createElementNS('http://www.w3.org/2000/svg', 'stop');
      stop2.setAttribute('offset', '100%');
      stop2.setAttribute('stop-color', background_color);
      new_gradient.appendChild(stop2);
      //Add gradient to the path
      path.style.fill = 'url(#' + new_gradient.id + ')';
      //Animate Stop 1
      stop1.animate(
        [
          { stopColor: background_color },
          { stopColor: stop_1_color }
        ],
        {
          duration: 2000,
          fill: 'forwards',
        }
      );
      //Animate Stop 2
      stop2.animate(
        [
          { stopColor: background_color },
          { stopColor: stop_2_color }
        ],
        {
          duration: 1000,
          fill: 'forwards',
        }
      );
    }

    function animateFill(path, animation_duration = 1000, animation_delay = 0) {
      //save fill to replace later
      let original_fill = path.style.fill;
      //make leaf transparent
      path.style.fill = 'transparent';
      //call animatPath
      animatePath(path, animation_duration, animation_delay);
      //get appropriate Timeout duration for execution of fill
      fill_delay = animation_duration + animation_delay;
      setTimeout(function() {
        if(path.classList.contains('leaf')) {
          animateGradient(path, 'transparent', '#8FAF8F', '#3C493C');
        }
        if(path.classList.contains('petal')) {
          animateGradient(path, 'transparent', '#CEA5E4', '#89619E', '50%');
        }
      }, fill_delay);
    }

    function animateSeedHead(path, animation_delay = 0) {
      path.style.transform = 'scale(0)';
      path.style.display = 'unset';
      //get bounding box data for transform origin
      const bounding_box = path.getBBox();
      const x = bounding_box.x + bounding_box.width / 2;
      const y = bounding_box.y + bounding_box.height;
      path.style.transformOrigin = `${x}px ${y}px`;

      path.animate([
        { transform: 'scale(0)' },
        { transform: 'scale(1)' }
      ], {
        duration: 1000,
        delay: animation_delay,
        easing: 'ease-in-out',
        fill: 'forwards'
      });
    }

    function animateLeaves(stem_group, animation_start = 0) {
      const leaves = stem_group.querySelectorAll('.leaf');
      let delay_between_leaves = 500;
      setTimeout(function() {
        leaves.forEach((leaf) => {
          animateFill(leaf, 500, delay_between_leaves);
          delay_between_leaves += 500;
        });
      }, animation_start );
    }

    function animateFlower(flower_group, animation_delay = 5850) {
      const seed_head = flower_group.querySelector('.seed-head');
      animateSeedHead(seed_head, animation_delay );
      
      const petals = flower_group.querySelectorAll('.petal');
      let animation_between_petal_delay = 0;
      petals.forEach((petal) => {
        setTimeout(function() {
          animateFill(petal, 500, animation_delay + 750);
        }, animation_between_petal_delay);
        animation_between_petal_delay = animation_between_petal_delay + 250;
      });
    }

    /**
     * Begin Animation declarations
     *
    */

    //Stem One Animations
    const stem_one = document.getElementById('stem_one');
    const stem_group_one = document.getElementById('stem_group_one');
    const flower_group_one = document.getElementById('flower_one')
    animatePath(stem_one, 7000);
    animateLeaves(stem_group_one, 500);
    animateFlower(flower_group_one);

    //Stem Two Animations
    const stem_two = document.getElementById('stem_two');
    const stem_group_two = document.getElementById('stem_group_two');
    const flower_group_two = document.getElementById('flower_two')
    animatePath(stem_two, 7000, 2000);
    animateLeaves(stem_group_two, 4000);
    animateFlower(flower_group_two, 8000);

    //Stem Three Animations
    const stem_three = document.getElementById('stem_three');
    const stem_group_three = document.getElementById('stem_group_three');
    const flower_group_three = document.getElementById('flower_three')
    animatePath(stem_three, 7000, 4000);
    animateLeaves(stem_group_three, 6000);
    animateFlower(flower_group_three, 10000);

    const branches = document.querySelectorAll('.stem-group');

    setTimeout(function() {
      branches.forEach((branch, i) => {
        const timing = {
          duration: 2000 + Math.random() * 1000, // Randomize duration slightly
          iterations: Infinity,
          direction: 'alternate',
          easing: 'ease-in-out'
        };

        const keyframes = [
          { transform: 'rotate(0deg)' },
          { transform: `rotate(${Math.random() * 5 - 2.5}deg)` }, // Randomize angle
        ];

        branch.animate(keyframes, timing);
      });
    }, 14000)

  </script>
</svg>
